import Apcard from "./apcard";
import Header from "./header";
import axios from "axios";

function Landing() {
    const [devices, setDevices] = useState([]);

    const handleSubmission = async(e) =>{
        e.preventDefault();
        const parameter = e.target.dev_par.value;
        const count = e.target.dev_count.value;
        const health = e.target.dev_health.value;

        try {
            const response = await axios.post('http://localhost:4567/devicedet', { parameter:parameter, count: count, health:health });
            fetchDevices();
        } catch (err) {
            console.error(err);
        }
    };

    useEffect(() => {
        fetchDevices();
      }, []);

    return (
        <div>
            <Header />
            <br />
            <h2 style={{marginLeft : '20px'}}>Device Health</h2>
            <br />
            <div style={styles.formcontainer}>
                <form onSubmit={handleSubmission}>
                    <div class="row">
                        <div class="col-3">
                            <input type="text" class="form-control" id="dev_par" placeholder="Device Parameter" required/>
                        </div>
                        <div class="col-3">
                            <input type="text" class="form-control" id="dev_count" placeholder="Count of Up Devices" required/>
                        </div>
                        <div class="col-4">
                            <input type="text" class="form-control" id="dev_health" placeholder="Health of Running Devices" required/>
                        </div>
                        <div class="col-2">
                            {/* <input type="submit" class="form-control" placeholder="Health of Running Devices" required/>*/}
                            <button type="submit" className="btn btn-primary w-100">Submit</button>
                        </div>
                    </div>
                </form>
            </div>
            <div style={styles.container}>

            </div>
        </div>
    );
}

export default Landing;

const styles = {
    container: {
        display: 'grid',
        gridTemplateColumns: 'repeat(3, 1fr)', 
        gap: '20px',
        marginTop: '20px',
    },
    formcontainer: {
        display: 'flex',
        flexWrap: 'wrap',
        gap: '20px',
        marginTop: '20px',
        marginLeft : '20px',
    },
    card: {
        border: '1px solid #ddd',
        borderRadius: '8px',
        boxShadow: '0 2px 8px rgba(0, 0, 0, 0.29)',
        padding: '15px',
        flex: 1,
    },
};
