import React from 'react';

function SimpleCard(props) {
  return (
    <div style={styles.card}>
      <div style={styles.cardBody}>
        <h5 style={styles.heading}>{props.heading}</h5>
        <p style={styles.content}>{props.count}</p>
        <p style={styles.content}>{props.status}</p>
      </div>
      <div style={styles.cardFooter}>
        <button style={styles.button}>Read More</button>
      </div>
    </div>
  );
}

const styles = {
  card: {
    width: '300px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.29)',
    marginTop: '20px',
    backgroundColor: ' rgba(177, 253, 119, 0.58)',
    overflow: 'hidden',
  },
  cardBody: {
    padding: '20px',
  },
  heading: {
    fontSize: '1.25rem',
    fontWeight: 'bold',
    marginBottom: '10px',
    color: '#333',
  },
  content: {
    fontSize: '1rem',
    color: '#666',
  },
  cardFooter: {
    padding: '5px 20px',
    backgroundColor: '#f5f5f5',
    textAlign: 'center',
  },
  button: {
    padding: '10px 20px',
    fontSize: '1rem',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  },
};

export default SimpleCard;
