import React from 'react';

function Apcard(props) {
  const cardBgColor = props.status === 'Healthy' ? 'rgba(177, 253, 119, 0.58)' : props.status === 'Poor'?'rgba(250, 48, 12, 0.8)':'rgba(250, 119, 12, 0.85)'; // Red if unhealthy
  return (
    <div style={styles.card}>
      <div style={styles.cardBody}>
        <h5 style={styles.heading}>{props.heading}</h5>
        <p style={styles.content}>{props.count}</p>
        <p style={styles.content}>{props.status}</p>
      </div>
      <div style={styles.cardFooter}>
        <button style={styles.button}>Read More</button>
      </div>
    </div>
  );
}

const styles = {
    card: {
    width: '300px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.29)',
    marginTop: '20px',
    // backgroundColor: ' rgba(177, 253, 119, 0.58)',
    backgroundColor: cardBgColor,
    overflow: 'hidden',
    },
    cardBody: {
    padding: '20px',
    },
    heading: {
    fontSize: '1.25rem',
    fontWeight: 'bold',
    marginBottom: '10px',
    color: '#333',
    },
    content: {
    fontSize: '1rem',
    color: '#666',
    },
    cardFooter: {
    padding: '5px 10px',
    backgroundColor: 'rgba(255, 255, 255, 0.58)',
    textAlign: 'center',
    },
    button: {
    padding: '5px 10px',
    fontSize: '1rem',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
    },
};

export default Apcard;
